def show_instructions():
    print('************* Welcome to Redemption of the rogue sorcerer! ******************')
    print('\n  * Collect all 9 Letters to unlock the ultimate spell to defeat the Demon King Amon')
    print('\n  * Move Commands: go North, go South, go East, go West')
    print("\n  * Add to Inventory: get 'letter'")
    print('\n  * Type "quit" to end the game at any time.')
    print("\n**********************************************************************************")


def player_status(rooms, current_room, inventory):
    print('\nYou are in', current_room)
    print('Inventory:', inventory)
    if 'spell' in rooms[current_room]:
        print("You see the letter ", rooms[current_room]['spell'])
    print('---------------------------')


def move_between_rooms(current_room, move, rooms):
    current_room = rooms[current_room][move]
    return current_room


def add_to_inventory(spell, inventory, rooms, current_room):
    inventory.append(spell)
    del rooms[current_room]['spell']
    return inventory


